import React, {useState, useEffect, useContext} from 'react';
import PropTypes from 'prop-types';
import NavBar from '../components/NavBar';
import { Container, Row, Col, Card, Spinner, Image } from 'react-bootstrap';
import { LanguageContext } from '../components/LanguageContext';
const AboutUs = props => {
    const [teamMembers, setTeamMembers] = useState([]);
    const [loading, setLoading] = useState(true);
    useEffect(() => {
        // Fetch team data from an API
        // Replace with your actual API endpoint
        const fetchTeamData = async () => {
          try {
            // In a real app, this would be your API endpoint
            // const response = await fetch('https://your-api.com/team');
            // const data = await response.json();

            // For demonstration, using mock data
            const mockData = [
              {
                id: 1,
                name: 'Steven',
                description: "I'm Steve, person who organize a painting workshop to express your mood and personality",
                avatarPlaceholder: true
              },
              {
                id: 2,
                name: 'Tommy',
                description: "My name is Tommy, I proud to present you that I can read your mind by letting you choose your coffee and shape of cup.",
                avatarPlaceholder: true
              }
            ];

            // Simulate API delay
            // setTimeout(() => {
            //   setTeamMembers(mockData);
            //   setLoading(false);
            // }, 800);
            setTimeout(() => {
                setTeamMembers(mockData);
                setLoading(false);
              }, 800);
          } catch (err) {
            // setError('Failed to load team data');
            // setLoading(false);
          }
        };

        fetchTeamData();
      }, []);
      return (
        <div>
          <NavBar />
          <Container className="mt-4">
            <h4 className="custom-font mb-4">พวกเราคือ</h4>

            {loading ? (
              <div className="text-center">
                <Spinner animation="border" />
              </div>
            ) : (
              <Row>
                {teamMembers.map((member) => (
                  <Col key={member.id} md={6} lg={4} className="mb-4">
                    <Card className="h-100 shadow-sm border-0">
                      <Card.Body className="text-center">
                        <Image
                          src="https://via.placeholder.com/120"
                          roundedCircle
                          className="mb-3"
                          alt="avatar"
                        />
                        <Card.Title>{member.name}</Card.Title>
                        <Card.Text>{member.description}</Card.Text>
                      </Card.Body>
                    </Card>
                  </Col>
                ))}
              </Row>
            )}
          </Container>
        </div>
      );
};


export default AboutUs;
